CREATE TRIGGER update_task_dates_on_subtask_insert
AFTER INSERT ON subtasks
FOR EACH ROW
BEGIN
    UPDATE tasks
    SET 
        date_started = (SELECT MIN(start_date) FROM subtasks WHERE task_id = NEW.task_id),
        date_due = (SELECT MAX(due_date) FROM subtasks WHERE task_id = NEW.task_id)
    WHERE id = NEW.task_id;
END;
CREATE TRIGGER update_task_dates_on_subtask_update
AFTER UPDATE ON subtasks
FOR EACH ROW
BEGIN
    UPDATE tasks
    SET 
        date_started = (SELECT MIN(start_date) FROM subtasks WHERE task_id = NEW.task_id),
        date_due = (SELECT MAX(due_date) FROM subtasks WHERE task_id = NEW.task_id)
    WHERE id = NEW.task_id;
END;
CREATE TRIGGER update_task_dates_on_subtask_delete
AFTER DELETE ON subtasks
FOR EACH ROW
BEGIN
    UPDATE tasks
    SET 
        date_started = (SELECT MIN(start_date) FROM subtasks WHERE task_id = OLD.task_id),
        date_due = (SELECT MAX(due_date) FROM subtasks WHERE task_id = OLD.task_id)
    WHERE id = OLD.task_id;
END;
